// components
import Button from "./Button";

// dependencies / hooks
import { motion } from "framer-motion";
import { useState } from "react";
import { useForm,useController  } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import Select from 'react-select';
import Header from "./Header";


const RegisterForm = () => {
	const navigate = useNavigate();
	const [visiblePassword, setVisiblePassword] = useState({
		password: false,
		passwordRetry: false,
	});

	const handlePasswordType = (passwordInput) => {
		setVisiblePassword({
			...visiblePassword,
			[passwordInput]: visiblePassword[passwordInput] ? false : true,
		});
	};

const[skey,setskey]=useState("1");

const handlesc=(e)=>{
setskey(e.target.value);
console.log(skey);
}
	


	// destructuring useForm
	const {
		register,
		handleSubmit,
		watch,
		formState: { errors },
		control
	} = useForm({
		mode: "onBlur",
		reValidateMode: "onChange",
	});

	const languageList = [
		{ value: 1, label: 'Admin' },
		{ value: 2, label: 'User' }
	  ];
	  const { field: { value: langValue, onChange: langOnChange, ...restLangField } } = useController({ name: 'usertype', control });

	const onSubmit = async(data) => {
	console.log(data);
    //data.preventDefault();
    if(langValue=="1"&&skey!="SUMITAGARWAL")
	{
		window.alert("Invalid secret key");
		
	}
	else{
    const { name, email, password ,usertype} = data;
	
    const res = await fetch("http://localhost:5000/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: name,
        email: email,
        password: password,
		usertype:usertype,
      }),
    });

	const dataa = await res.json();
 
    if (dataa.status === 400 || !dataa|| dataa.error) {
      window.alert("Invalid Registration");
      console.log("Invalid Registration");
    } else {
      window.alert("Successfull Registration");
      console.log("Successfull Registration");
    }
    // Redirect to the login page once the user is registered
    navigate("/login");
}
  };


	return (
		<motion.section
			className="register"
			initial={{ width: 0 }}
			animate={{ width: "auto", transition: { duration: 0.5 } }}
			exit={{ x: window.innerWidth, transition: { duration: 0.5 } }}
		>
			<img src="logo-blue.svg" alt="" />
			<p>
			Still not registered?<br /> So, before picking up your best friend, we need some data:
			
			</p>
			<form onSubmit={handleSubmit(onSubmit)}>
			



             







		
      <label>Select Role</label>
      <Select
        className='select-input'
        placeholder="Select User Type"
        isClearable
        options={languageList}
        value={langValue ? languageList.find(x => x.value === langValue) : langValue}
        onChange={option => langOnChange(option ? option.value : option)}
        {...restLangField}
      />
      {errors.usertype && <p>{errors.usertype.message}</p>}



    { langValue=="1"?<div>
	  <label htmlFor="name">Secret Key</label>
				<input
					id="sc"
					type="text"
					{...register("sc", {
						required: "É necessário informar seu nome",
						maxLength: {
							value: 25,
							message: "O número máximo de caracteres é 25",
						},
					})}
					placeholder="Digite seu nome completo"
					onChange={handlesc}
				/>
				{errors.sc&& <p className="error">{errors.sc.message}</p>}
				</div>:null}


	
				<label htmlFor="name">Nome</label>
				<input
					id="name"
					type="text"
					{...register("name", {
						required: "É necessário informar seu nome",
						maxLength: {
							value: 25,
							message: "O número máximo de caracteres é 25",
						},
					})}
					placeholder="Digite seu nome completo"
				/>
				{errors.name && <p className="error">{errors.name.message}</p>}

				<label htmlFor="email">E-mail</label>
				<input
					id="email"
					type="email"
					{...register("email", {
						required: "É necessário informar um endereço de email",
						pattern:
							/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/,
					})}
					placeholder="Escolha seu melhor email"
				/>
				{errors.email && (
					<p className="error">
						{errors.email.message || "Por favor, verifique o email digitado"}
					</p>
				)}

				<label htmlFor="pass-create">Senha</label>
				<span>
					<span
						onClick={() => handlePasswordType("password")}
						className="pass__view"
					></span>
					<input
						id="pass-create"
						type={visiblePassword.password ? "text" : "password"}
						{...register("password", {
							required: "Crie uma senha",
							pattern: /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,15}$/,
						})}
						placeholder="Crie uma senha"
					/>
				</span>
				{errors.password && (
					<p className="error">
						{errors.password.message ||
							"A senha deve conter pelo menos uma letra maiúscula, um número e ter entre 6 e 15 caracteres"}
					</p>
				)}

				<label htmlFor="pass-confirm">Confirme sua senha</label>
				<span>
					<span
						onClick={() => handlePasswordType("passwordRetry")}
						className="pass__view"
					></span>
					<input
						id="pass-confirm"
						type={visiblePassword.passwordRetry ? "text" : "password"}
						{...register("confirm_password", {
							required: "Repita a senha criada acima",
							validate: (value) => {
								if (watch("password") !== value) {
									return "As senhas não batem";
								}
							},
						})}
						placeholder="Repita a senha criada acima"
					/>
				</span>
				{errors.confirm_password && (
					<p className="error">{errors.confirm_password.message}</p>
				)}

				<Button type="submit" children="Cadastrar" />
			</form>
		</motion.section>
	);
};

export default RegisterForm;
