// dependencies
import React, { useEffect } from "react";
import { motion } from 'framer-motion';
import { useNavigate } from "react-router-dom";
// assets
import { pets } from '../data/data.js';
import { Link } from 'react-router-dom';
import useMediaQuery from '../hooks/useMediaQuery';

// hooks and dependencies
import { Helmet } from 'react-helmet';
// components
import CardPet from './CardPet.js';

import Cookies from "universal-cookie";



const AdminHome = () => {
  const matches = useMediaQuery('(max-width: 767px)');
  const navigate = useNavigate();
  const cookies = new Cookies();
  const token = cookies.get("jwtoken");
  const login = async () => {

    try {
      const res = await fetch("http://localhost:5000/afterlogin", {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        
      });
     
      if (res.status === 401) {
      
        const error = new Error(res.error);
        throw error;
      }
      const data = await res.json();
      console.log(data);
      
      
    } catch (err) {
      navigate("/login");
      
     // console.log(err);
      
    }
  };

  useEffect(() => {
    login();
  }, []);


  return (
    
    <motion.section className='home' initial={{ width: 0 }} animate={{ width: "auto", transition: { duration: 0.5 } }} exit={{ x: window.innerWidth, transition: { duration: 0.5 } }}>
    <Helmet>
      <style>{"body { background-color: #3874ff; }"}</style>
    </Helmet>
    <img src="logo-clear.svg" alt="Logo AdoPet" />
    <h3>Hello Admin!</h3>
    <p>
      {matches ? 'Que tal mudar sua vida adotando seu novo melhor amigo? Vem com a gente!' : 'What do you want to do?'}
    </p>
    <div className='home__buttons'>
      <Link className='button' to='/home'>View Animal List</Link>
      
    </div>
    <div className='home__buttons'>
      <Link className='button' to='/home'>See Responses</Link>
      
    </div>
  </motion.section >
   
  );
};

export default AdminHome;
