// dependencies
import React, { useEffect } from "react";
import { motion } from 'framer-motion';
import { useNavigate } from "react-router-dom";
// assets
import { pets } from '../data/data.js';

// components
import CardPet from './CardPet.js';

import Cookies from "universal-cookie";



const Home = () => {

  const navigate = useNavigate();
  const cookies = new Cookies();
  const token = cookies.get("jwtoken");
  const login = async () => {
console.log("hello");
    try {
      const res = await fetch("http://localhost:5000/afterlogin", {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        
      });
      const data = await res.json();
      console.log(data);
      
      if (!res.status === 200) {
        const error = new Error(res.error);
        throw error;
      }
    } catch (err) {
      console.log(err);
      
    }
  };

  useEffect(() => {
    login();
  }, []);


  return (
    <motion.section className='home' initial={{ width: 0 }} animate={{ width: "auto", transition: { duration: 0.5 } }} exit={{ x: window.innerWidth, transition: { duration: 0.5 } }}>
      <p>Olá! <br /> Veja os amigos disponíveis para adoção!</p>
      <div className='cards'>
        {
          pets.map((pet, i) => (
            <CardPet
              age={pet.age}
              size={pet.size}
              behavior={pet.behavior}
              city={pet.city}
              name={pet.name}
              img={pet.img}
              key={i}
            />
          ))
        }
      </div>
    </motion.section >
  );
};

export default Home;
