// dependencies
import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import useState from 'react-usestateref'
import { motion } from 'framer-motion';
import { useNavigate } from "react-router-dom";
// assets
//import { pets } from '../src/data/data.js';

// components
import CardPet from '../CardPet.js';

import Cookies from "universal-cookie";

import Header from "../Header";
import Swal from 'sweetalert2'

const Responses = () => {
    const [petsdata,setpetsdata,ref]=useState([{}]);

    
    const getanimals = async () => {

        try {
          const res = await fetch("http://localhost:5000/getquestions", {
            method: "GET",
            headers: {
              Accept: "application/json",
              "Content-Type": "application/json",
            },
            
          });
         
          if (res.status === 401) {
          
            const error = new Error(res.error);
            throw error;
          }
          const data = await res.json();
          setpetsdata(data);
          console.log(ref.current);
          
          
        } catch (err) {
          navigate("/login");
          
        }
      };

    




  const navigate = useNavigate();
  const cookies = new Cookies();
  const token = cookies.get("jwtoken");
  useEffect(() => {
    window.scrollTo(0, 0)
  }, [])
  const login = async () => {

    try {
      const res = await fetch("http://localhost:5000/afterlogin", {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        
      });
     
      if (res.status === 401) {
      
        const error = new Error(res.error);
        throw error;
      }
      const data = await res.json();
      console.log(data);
      
      
    } catch (err) {
      navigate("/login");
      
     // console.log(err);
      
    }
  };



  

  const handleclick1 =async (elem, idx) => {
   
   
      try {
        const res = await fetch(`http://localhost:5000/deletequestion/${elem._id}`, {
          method: "DELETE",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          
        });
       
        if (res.status === 401) {
        
          const error = new Error(res.error);
          throw error;
        }
        Swal.fire({
          title: 'Success!',
          text: 'Pet Deleted',
          icon: 'success',
          confirmButtonText: 'Success',
          timer: '2000'
          })
        getanimals();
        
      } catch (err) {
        Swal.fire({
          title: 'Error!',
          text: 'Deletion Failed',
          icon: 'error',
          confirmButtonText: 'Retry'
          })
      }
  };

  const handleclick =async (elem, idx) => {


    Swal.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      type: 'warning',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Confirm!'
  }).then((result) => {
    if (result.dismiss !== 'cancel') {

      handleclick1(elem,idx);
    }
   else
    {
       

    }
})
 
   
  };
  
  

  useEffect(() => {
    login();
    getanimals();
  }, []);


  return (
    <>
       <Header/>
   
    <motion.section className='home' initial={{ width: 0 }} animate={{ width: "auto", transition: { duration: 0.5 } }} exit={{ x: window.innerWidth, transition: { duration: 0.5 } }}>
      <p>Olá! <br /> Veja os amigos disponíveis para adoção!</p>
      <div className='cards'>
        {
          ref.current.map((pet, i) => (
            <div key={i} className='card'>
            <img src={pet.img} alt={pet.name} />
            <h4>{pet.name}</h4>
            <ul>
              <li>{pet.phone}</li>
              <li>{pet.email}</li>
              <li>{pet.animalname}</li>
                
              </ul>
            
              <p className='card__city'>{pet.question}</p>
              <Link className='card__contact' to="/getuserdetail"  state={pet}>Get More Details About User</Link>
              <button className='cpb' onClick={() => { handleclick(pet, i); }}>Delete Question</button>
            </div>
          ))
        }
      </div>
    </motion.section >
    </>
  );
};

export default Responses;
