// dependencies
import { motion } from "framer-motion";
import { useEffect, useContext,useState } from "react";
import { useForm,useController  } from "react-hook-form";
import Cookies from 'universal-cookie'
import { useNavigate } from "react-router-dom";
// components
import Button from "./Button.js";
import axios from "axios";



const Payment = () => {

	const navigate = useNavigate();

	
	// destructuring useForm
	const {
		register,
		handleSubmit,
		formState: { errors },
		
	} = useForm({
		mode: "onBlur",
		reValidateMode: "onChange",
	});

	const handleOpenRazorpay = (data) => {

        const options = {
            key: 'rzp_test_9e9h7K87g5yKdi',
            amount: Number(data.amount),
            currency: data.currency,
            order_id: data.id,
            name: 'SHOPPING APP',//
            description: 'XYZ',//
            handler: function (response) {
                console.log(response, "34")
                axios.post('http://localhost:5000/verify', { response: response })
                    .then(res => {
                        console.log(res, "37")
                        // your orders
                    })
                    .catch(err => {
                        console.log(err)
                    })
            }

        }
        const rzp = new window.Razorpay(options)
        rzp.open()

    }

	   const onSubmit = (amount) => {
        const _data = { amount: amount }
        axios.post('http://localhost:5000/orders', _data)
            .then(res => {
                console.log(res.data, "29")
                handleOpenRazorpay(res.data.data)
            })
            .catch(err => {
                console.log(err)
            })
    }


	return (
		<motion.section
			className="register"
			initial={{ width: 0 }}
			animate={{ width: "100%", transition: { duration: 0.5 } }}
			exit={{ x: window.innerWidth, transition: { duration: 0.5 } }}
		>
			<img src="logo-blue.svg" alt="" />
			<p>Help us save mankind</p>
			<form onSubmit={handleSubmit(onSubmit)}>


			<label htmlFor="inr">Enter Amount</label>
				<input
					id="inr"
					type="Number"
					{...register("inr", {
						required: "É necessário informar um endereço de email",
						
					})}
					placeholder="0.0"
				/>
				{errors.inr && (
					<p className="error">
						{errors.inr.message || "Por favor, verifique o email digitado"}
					</p>
				)}

				

				
				<Button type="submit" children="Donateee" />
				
			</form>
		</motion.section>
	);
};

export default Payment;
