// dependencies
import { motion } from "framer-motion";
import { useEffect, useContext,useState } from "react";
import { useForm,useController  } from "react-hook-form";
import Cookies from 'universal-cookie'
import { useNavigate } from "react-router-dom";
// components
import Button from "./Button.js";




const LoginForm = () => {

	const navigate = useNavigate();

	
	// destructuring useForm
	const {
		register,
		handleSubmit,
		formState: { errors },
		control
	} = useForm({
		mode: "onBlur",
		reValidateMode: "onChange",
	});

	const onSubmit = async(data) => {
		
	    const { inr } = data;
		const res = await fetch("http://localhost:5000/login", {
		  method: "POST",
		  headers: {
			"Content-Type": "application/json",
		  },
		  //credentials: 'include',
		  body: JSON.stringify({
			inr
		  }),
		});
	
		const dataa = await res.json();
       
		
		  
		if (dataa.status === 400 || !dataa || dataa.error) {
		   window.alert("inSuccessfull login");
        console.log("inSuccessfull login");
			
		
		} else {
			
			navigate("/home");
			
		    window.alert("Successfull login");
        console.log("Successfull login");
		
		}
		
	  };

	  


	return (
		<motion.section
			className="register"
			initial={{ width: 0 }}
			animate={{ width: "100%", transition: { duration: 0.5 } }}
			exit={{ x: window.innerWidth, transition: { duration: 0.5 } }}
		>
			<img src="logo-blue.svg" alt="" />
			<p>Help us save mankind</p>
			<form onSubmit={handleSubmit(onSubmit)}>


			<label htmlFor="inr">Enter Amount</label>
				<input
					id="inr"
					type="Number"
					{...register("inr", {
						required: "É necessário informar um endereço de email",
						
					})}
					placeholder="0.0"
				/>
				{errors.inr && (
					<p className="error">
						{errors.inr.message || "Por favor, verifique o email digitado"}
					</p>
				)}

				

				
				<Button type="submit" children="Donate" />
				
			</form>
		</motion.section>
	);
};

export default LoginForm;
