// styles
import Button from '../Button';
import React, {  useEffect } from "react";
import useState from 'react-usestateref'
import { useNavigate } from "react-router-dom";
import Cookies from "universal-cookie";
// dependencies
import { useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useForm } from "react-hook-form";
import Header from "../Header";
// assets


const Addanimal = () => {
  const location = useLocation();
  // destructuring useForm

 
  const navigate = useNavigate();
  const cookies = new Cookies();
  const token = cookies.get("jwtoken");
  const login = async () => {

    try {
      const res = await fetch("http://localhost:5000/afterlogin", {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        
      });
     
      if (res.status === 401) {
      
        const error = new Error(res.error);
        throw error;
      }
      const data = await res.json();
      console.log(data);
     
     
      
    } catch (err) {
      navigate("/login");
      
     // console.log(err);
      
    }
  };

  useEffect(() => {
    login();
  }, []);

 

  const { register, handleSubmit, formState: { errors } } = useForm({
    mode: 'onBlur',
    reValidateMode: 'onChange'
  });



  const handleFileUpload = async (e) => {
  
    const file = e.target.files[0];
    const base64 = await convertToBase64(file);
    console.log(base64)
    setuserdata({ ...data, image : base64 })
    
  }



  const onSubmit = async(data) => {
   
    console.log(data);
    const { name, img,city,behaviour,size,age } = data;

    //   console.log(ref.current);
      const res = await fetch("http://localhost:5000/addanimal", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          
         name:name,
         img:img,
         city:city,
         behaviour:behaviour,
         size:size,
         age:age,

        }),
      });
  
    const dataa = await res.json();
   console.log(dataa);
   
    if(dataa.error||dataa.status === 422 || dataa.status===400|| dataa.status===404||dataa.status===500)
    {
        window.alert("Invalid Registration");
        console.log("Invalid Registration");
      } else {
        window.alert("Successfull Registration");
        console.log("Successfull Registration");
        navigate("/adminhome");
        
      }
      // Redirect to the login page once the user is registered
     
  
    };

  return (
    <>
      <Header/>
   
    <motion.section className='message' initial={{ width: 0 }} animate={{ width: "auto", transition: { duration: 0.5 } }} exit={{ x: window.innerWidth, transition: { duration: 0.5 } }}>
      {
        
          <>
        
            <p>Add a new friend to the list:</p>
            <form onSubmit={handleSubmit(onSubmit)}>
              <label htmlFor="name">Name of pet</label>
              <input id='name' type="text" {...register("name", { required: 'É necessário informar seu nome', maxLength: { value: 40, message: 'O número máximo de caracteres é 40' } })} placeholder='Insira seu nome completo' />
              {errors.name && <p className="error">{errors.name.message}</p>}
              


              <label htmlFor="img">Image</label>
              <input id='img' type="text" {...register("img", { required: 'É necessário informar seu nome', maxLength: { value: 40, message: 'O número máximo de caracteres é 40' } })} placeholder='Insira seu nome completo' />
              {errors.img && <p className="error">{errors.img.message}</p>}


              
              <label htmlFor="city">city</label>
              <input id='city' type="text" {...register("city", { required: 'É necessário informar o nome do animal', maxLength: { value: 25, message: 'O número máximo de caracteres é 25' } })} placeholder='Por qual animal você se interessou?' />
              {errors.city && <p className="error">{errors.city.message}</p>}


              <label htmlFor="behaviour">Behaviour</label>
              <textarea name="behaviour" id="behaviour" cols="30" rows="10" {...register('behaviour', { required: 'É necessário escrever uma mensagem', maxLength: { value: 500, message: 'O número máximo de caracteres é 500' } })} placeholder='Escreva sua mensagem.' spellCheck='false'></textarea>
              {errors.behaviour && <p className="error">{errors.behaviour.message}</p>}

              <label htmlFor="size">size</label>
              <input id='size' type="text" {...register("size", { required: 'É necessário informar seu nome', maxLength: { value: 40, message: 'O número máximo de caracteres é 40' } })} placeholder='Insira seu nome completo' />
              {errors.size && <p className="error">{errors.size.message}</p>}


              <label htmlFor="age">Age</label>
              <input id='age' type="text" {...register("age", { required: 'É necessário informar o nome do animal', maxLength: { value: 25, message: 'O número máximo de caracteres é 25' } })} placeholder='Por qual animal você se interessou?' />
              {errors.age && <p className="error">{errors.age.message}</p>}


              

              <Button type='submit' children='Enviar' />
            </form>
          </>
       
      }
    </motion.section>
    </>
  );
};
function convertToBase64(file){
  return new Promise((resolve, reject) => {
    const fileReader = new FileReader();
    fileReader.readAsDataURL(file);
    fileReader.onload = () => {
      resolve(fileReader.result)
    };
    fileReader.onerror = (error) => {
      reject(error)
    }
  })
}

export default Addanimal;
