// dependencies
import { createContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Cookies from "universal-cookie";
export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const navigate = useNavigate();

    // Num primeiro momento, usaremos dados fixos para o login/logout
    const [user, setUser] = useState(
		
	);
    const [loading, setLoading] = useState(true);
    const cookies = new Cookies();
	const token = cookies.get("jwtoken");
    useEffect(() => {
        const checkLoggedIn = async () => {
			

			try {
				const res = await fetch("http://localhost:5000/afterlogin", {
				method: "GET",
				headers: {
				Accept: "application/json",
				"Content-Type": "application/json",
				Authorization: `Bearer ${token}`,
				},
				
			});
			if (res.status === 401) {
      
				const error = new Error(res.error);
				throw error;
			  }
				const data = await res.json();
				
				setUser({

					data
					});
				 
				  
				
			  } catch (err) {
				console.log(err);
                navigate("/login");
				
				
			  }
		};
        checkLoggedIn();

        // to avoid page loading without properly gathering the user info from localStorage, we must use a state to wait for it. When the data fetch is ended, then we set Loading to false and then we render the page (this last one is made on Routes file)
        setLoading(false);
    }, []);

    const login = async(email, password,usertype) => {

        const res = await fetch("http://localhost:5000/login", {
		  method: "POST",
		  headers: {
			"Content-Type": "application/json",
		  },
		  //credentials: 'include',
		  body: JSON.stringify({
			email,
			password,
			usertype
		  }),
		});
	
		const dataa = await res.json();
       
         console.log(dataa);
		
		 cookies.set("jwtoken", dataa.token, {
			expires: new Date(Date.now() + 25892000000),
			//httpOnly: true,
			path: "/",
		  });
		  
		  
		if (dataa.status === 400 || !dataa || dataa.error) {
		   window.alert("inSuccessfull login");
        console.log("inSuccessfull login");
			
		
		} else {
			console.log(dataa.data);
			setUser(dataa.data);
			console.log(usertype);
			if(usertype=="2")
			navigate("/home");
			else
			navigate("/adminhome")
		    window.alert("Successfull login");
        console.log("Successfull login");
		
		}



    };

    const logout = () => {
        console.log('logout');
        localStorage.removeItem('user');
        setUser(null);
        navigate('/');
    };

    // !!user:
    // user != null, then authenticated = true
    // user == null, then authenticated = false

    return (
        <AuthContext.Provider value={{ authenticated: !!user, user, loading, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};



const UserStateProvider = (props) => {
  const [state, setState] = useState({});

  const checkLoggedIn = async () => {
			

	try {
		const res = await fetch("http://localhost:5000/afterlogin", {
		method: "GET",
		headers: {
		Accept: "application/json",
		"Content-Type": "application/json",
		Authorization: `Bearer ${token}`,
		},
		
	});
	if (res.status === 401) {

		const error = new Error(res.error);
		throw error;
	  }
		const data = await res.json();
		
		setstate({state
			});
		 
		  
		
	  } catch (err) {
		console.log(err);
		navigate("/login");
		
		
	  }
};


  useEffect(() => {
	checkLoggedIn();
  }, []);

  return (
    <UserContext.Provider value={{state, checkLoggedIn}}>{props.children}</UserContext.Provider>
  );
};

export default UserStateProvider;
