// dependencies
import { motion } from "framer-motion";
import { useEffect, useContext,useState } from "react";
import { useForm,useController  } from "react-hook-form";
import Cookies from 'universal-cookie'
import { useNavigate } from "react-router-dom";
// components
import Button from "./Button.js";
import axios from "axios";



const LoginForm = () => {

	const navigate = useNavigate();

	
	// destructuring useForm
	const {
		register,
		handleSubmit,
		formState: { errors },
		control
	} = useForm({
		mode: "onBlur",
		reValidateMode: "onChange",
	});

	const onSubmit = async(data) => {
		
	    const { inr } = data;
        const { data: { key } } = await axios.get("http://www.localhost:5000/getkey")

        const { data: { order } } = await axios.post("http://localhost:5000/api/checkout", {
            amount
        })

        const options = {
            key,
            amount: order.amount,
            currency: "INR",
            name: "6 Pack Programmer",
            description: "Tutorial of RazorPay",
            image: "https://avatars.githubusercontent.com/u/25058652?v=4",
            order_id: order.id,
            callback_url: "http://localhost:4000/api/paymentverification",
            prefill: {
                name: "Gaurav Kumar",
                email: "gaurav.kumar@example.com",
                contact: "9999999999"
            },
            notes: {
                "address": "Razorpay Corporate Office"
            },
            theme: {
                "color": "#121212"
            }
        };
        const razor = new window.Razorpay(options);
        razor.open();
	  };

	  


	return (
		<motion.section
			className="register"
			initial={{ width: 0 }}
			animate={{ width: "100%", transition: { duration: 0.5 } }}
			exit={{ x: window.innerWidth, transition: { duration: 0.5 } }}
		>
			<img src="logo-blue.svg" alt="" />
			<p>Help us save mankind</p>
			<form onSubmit={handleSubmit(onSubmit)}>


			<label htmlFor="inr">Enter Amount</label>
				<input
					id="inr"
					type="Number"
					{...register("inr", {
						required: "É necessário informar um endereço de email",
						
					})}
					placeholder="0.0"
				/>
				{errors.inr && (
					<p className="error">
						{errors.inr.message || "Por favor, verifique o email digitado"}
					</p>
				)}

				

				
				<Button type="submit" children="Donate" />
				
			</form>
		</motion.section>
	);
};

export default LoginForm;
