import { Link } from "react-router-dom";
//import Button from "./Button";

const CardPet = ({ age, size, behavior, city, name, img, index }) => {


  const FetchChangedChangedAttendanceDetails = (elem, idx) => {
    console.log(props.Subject + "dubiu");
    axios
      .get(`http://localhost:3002/detailstloginusers/${elem.email}`)
      .then((res) => {
        console.log(res.data[props.Subject]);
        // console.log(props.Subject+"dubiu");
        //console.log(res.data.Data_Structures);
        if (absent[idx][1] === "1") {
          if (absent[idx][1] === "1") {
            Setabsentcount((absentcount) => ({
              ...absentcount,
              [idx]: (
                ((totalnoofclasses - res.data[props.Subject].length) /
                  totalnoofclasses) *
                100
              ).toFixed(2),
            }));
          } else {
            Setabsentcount((absentcount) => ({
              ...absentcount,
              [idx]: (
                ((totalnoofclasses - res.data[props.Subject].length - 1) /
                  totalnoofclasses) *
                100
              ).toFixed(2),
            }));
          }
        } else {
          Setabsentcount((absentcount) => ({
            ...absentcount,
            [idx]: (
              ((totalnoofclasses - res.data[props.Subject].length - 1) /
                totalnoofclasses) *
              100
            ).toFixed(2),
          }));
        }
      })
      .catch((err) => {
        console.log("attendance calculation not possible ");
        console.log(err);
      });
  };

  return (
    <div key={index} className='card'>
      <img src={img} alt={name} />
      <h4>{name}</h4>
      <ul>
        <li>{age}</li>
        <li>{size}</li>
        <li>{behavior}</li>
        
      </ul>
     
      <p className='card__city'>{city}</p>
      <Link className='card__contact' to="/mensagem" aria-label='Falar com responsável'>Falar com responsável</Link>
      <button className='cpb' onClick={() => {
                              HandleAbsentees(elem, idx);
                              
                            }}>Delete</button>
    </div>
  );
};

export default CardPet;
