// dependencies
import React, { useEffect ,useContext} from "react";
import { Link } from "react-router-dom";
import useState from 'react-usestateref'
import { motion } from 'framer-motion';
import { useNavigate } from "react-router-dom";
import UserContext from "../contexts/usercontext.js";
// assets
//import { pets } from '../src/data/data.js';

// components


import Cookies from "universal-cookie";




const Viewanimal = () => {
    const [petsdata,setpetsdata,ref]=useState([{}]);


    const [data, setData] = useState({});
    const [isLoading, setIsLoading] = useState(false);
    useEffect(async () => {
      await setIsLoading(true);
      const {currentUser, setCurrentUser,reff}=useContext(UserContext);
      setData(setCurrentUser);
      await setIsLoading(false);
  }, []);

   
    const getanimals = async () => {

        try {
          const res = await fetch("http://localhost:5000/getanimals", {
            method: "GET",
            headers: {
              Accept: "application/json",
              "Content-Type": "application/json",
            },
            
          });
         
          if (res.status === 401) {
          
            const error = new Error(res.error);
            throw error;
          }
          const data = await res.json();
          setpetsdata(data);
          console.log(ref.current);
          
          
        } catch (err) {
          navigate("/login");
          
        }
      };

    




  const navigate = useNavigate();
  

     
console.log(currentUser);
  useEffect(() => {

    getanimals();
  }, []);


  return (
    <motion.section className='home' initial={{ width: 0 }} animate={{ width: "auto", transition: { duration: 0.5 } }} exit={{ x: window.innerWidth, transition: { duration: 0.5 } }}>
      <p>Olá! {data.name}<br /> Veja os amigos disponíveis para adoção!</p>
      <div className='cards'>
        {
          ref.current.map((pet, i) => (
            <div key={i} className='card'>
            <img src={pet.img} alt={pet.name} />
            <h4>{pet.name}</h4>
            <ul>
              <li>{pet.age}</li>
              <li>{pet.size}</li>
              <li>{pet.behavior}</li>
                
              </ul>
            
              <p className='card__city'>{pet.city}</p>
              <Link className='card__contact' to="/querypage" aria-label='Falar com responsável' state={pet}>Falar com responsável</Link>
             
            </div>
          ))
        }
      </div>
    </motion.section >
  );
};

export default Viewanimal;
