// dependencies
import React, { useEffect } from "react";

import useState from 'react-usestateref'
import { motion } from 'framer-motion';
import { useNavigate } from "react-router-dom";
// assets
//import { pets } from '../src/data/data.js';

// components
import CardPet from '../CardPet.js';

import Cookies from "universal-cookie";



const pets = [
    { img: 'Dunga.png', name: 'Dunga', age: '2 anos', size: 'Porte pequeno', behavior: 'Calmo e educado', city: 'Rio de Janeiro (RJ)' },
    { img: 'Felicia.png', name: 'Felícia', age: '3 meses', size: 'Porte pequeno', behavior: 'Ativa e carinhosa', city: 'Nova Iguaçu (RJ)' },
    { img: 'Sirius.png', name: 'Sirius', age: '6 meses', size: 'Porte grande', behavior: 'Ativa e educado', city: 'Duque de Caxias (RJ)' },
    { img: 'Fiona.png', name: 'Fiona', age: '3 anos', size: 'Porte pequeno', behavior: 'Calma e carinhosa', city: 'São Gonçalo (RJ)' },
    { img: 'Sid.png', name: 'Sid', age: '8 meses', size: 'Porte médio/grande', behavior: 'Brincalhão e amável', city: 'Rio de Janeiro (RJ)' },
    { img: 'Yoda.png', name: 'Yoda', age: '1 ano', size: 'Porte médio', behavior: 'Ativo e carinhoso', city: 'Nova Iguaçu (RJ)' },
    { img: 'Lua.png', name: 'Lua', age: '6 meses', size: 'Porte médio', behavior: 'Ativo e carinhosa', city: 'Duque de Caxias (RJ)' },
    { img: 'Amora.png', name: 'Amora', age: '45 dias', size: 'Porte grande', behavior: 'Calma e carinhosa', city: 'São Gonçalo (RJ)' },
    { img: 'Zelda.png', name: 'Zelda', age: '5 meses', size: 'Porte médio', behavior: 'Ativa e amável', city: 'Rio de Janeiro (RJ)' },
  ];

const Viewanimal = () => {

  const navigate = useNavigate();
  const cookies = new Cookies();
  const token = cookies.get("jwtoken");
  const login = async () => {

    try {
      const res = await fetch("http://localhost:5000/afterlogin", {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        
      });
     
      if (res.status === 401) {
      
        const error = new Error(res.error);
        throw error;
      }
      const data = await res.json();
      console.log(data);
      
      
    } catch (err) {
      navigate("/login");
      
     // console.log(err);
      
    }
  };

  useEffect(() => {
    login();
  }, []);


  return (
    <motion.section className='home' initial={{ width: 0 }} animate={{ width: "auto", transition: { duration: 0.5 } }} exit={{ x: window.innerWidth, transition: { duration: 0.5 } }}>
      <p>Olá! <br /> Veja os amigos disponíveis para adoção!</p>
      <div className='cards'>
        {
          pets.map((pet, i) => (
            <CardPet
              age={pet.age}
              size={pet.size}
              behavior={pet.behavior}
              city={pet.city}
              name={pet.name}
              img={pet.img}
              key={i}
            />
          ))
        }
      </div>
    </motion.section >
  );
};

export default Viewanimal;
