// dependencies
import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import useState from 'react-usestateref'
import { motion } from 'framer-motion';
import { useNavigate } from "react-router-dom";
// assets
//import { pets } from '../src/data/data.js';

// components
import CardPet from '../CardPet.js';

import Cookies from "universal-cookie";




const Viewanimal = () => {
    const [petsdata,setpetsdata,ref]=useState([{}]);

    
    const getanimals = async () => {

        try {
          const res = await fetch("http://localhost:5000/getanimals", {
            method: "GET",
            headers: {
              Accept: "application/json",
              "Content-Type": "application/json",
            },
            
          });
         
          if (res.status === 401) {
          
            const error = new Error(res.error);
            throw error;
          }
          const data = await res.json();
          setpetsdata(data);
          console.log(ref.current);
          
          
        } catch (err) {
          navigate("/login");
          
        }
      };

    




  const navigate = useNavigate();
  const cookies = new Cookies();
  const token = cookies.get("jwtoken");
  const login = async () => {

    try {
      const res = await fetch("http://localhost:5000/afterlogin", {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        
      });
     
      if (res.status === 401) {
      
        const error = new Error(res.error);
        throw error;
      }
      const data = await res.json();
      console.log(data);
      
      
    } catch (err) {
      navigate("/login");
      
     // console.log(err);
      
    }
  };



  

  const handleclick = (elem, idx) => {
    console.log(props.Subject + "dubiu");
    axios
      .get(`http://localhost:3002/detailstloginusers/${elem.email}`)
      .then((res) => {
        console.log(res.data[props.Subject]);
        // console.log(props.Subject+"dubiu");
        //console.log(res.data.Data_Structures);
        if (absent[idx][1] === "1") {
          if (absent[idx][1] === "1") {
            Setabsentcount((absentcount) => ({
              ...absentcount,
              [idx]: (
                ((totalnoofclasses - res.data[props.Subject].length) /
                  totalnoofclasses) *
                100
              ).toFixed(2),
            }));
          } else {
            Setabsentcount((absentcount) => ({
              ...absentcount,
              [idx]: (
                ((totalnoofclasses - res.data[props.Subject].length - 1) /
                  totalnoofclasses) *
                100
              ).toFixed(2),
            }));
          }
        } else {
          Setabsentcount((absentcount) => ({
            ...absentcount,
            [idx]: (
              ((totalnoofclasses - res.data[props.Subject].length - 1) /
                totalnoofclasses) *
              100
            ).toFixed(2),
          }));
        }
      })
      .catch((err) => {
        console.log("attendance calculation not possible ");
        console.log(err);
      });
  };

  useEffect(() => {
    login();
    getanimals();
  }, []);


  return (
    <motion.section className='home' initial={{ width: 0 }} animate={{ width: "auto", transition: { duration: 0.5 } }} exit={{ x: window.innerWidth, transition: { duration: 0.5 } }}>
      <p>Olá! <br /> Veja os amigos disponíveis para adoção!</p>
      <div className='cards'>
        {
          ref.current.map((pet, i) => (
            <div key={i} className='card'>
            <img src={pet.img} alt={pet.name} />
            <h4>{pet.name}</h4>
            <ul>
              <li>{pet.age}</li>
              <li>{pet.size}</li>
              <li>{pet.behavior}</li>
                
              </ul>
            
              <p className='card__city'>{pet.city}</p>
              <Link className='card__contact' to="/mensagem" aria-label='Falar com responsável'>Falar com responsável</Link>
              <button className='cpb' onClick={() => { handleclick(pet, i); }}>Delete</button>
            </div>
          ))
        }
      </div>
    </motion.section >
  );
};

export default Viewanimal;
